import React, { useState } from 'react';

export const Add = () => {
  const [query, setQuery] = useState("");

  const onChange = (e) => {
    e.preventDefault();
    setQuery(e.target.value);

    // Use backticks for the URL to correctly use template literals
fetch(`http://www.omdbapi.com/?apikey=${process.env.REACT_APP_MY_KEY}&language=en-US&page=1&include_adult=false&query=${e.target.value}`)
    .then(res => res.json())
      .then((data) => {
        console.log(data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  };

  return (
    <div>
      <input
        type="text"
        value={query}
        onChange={onChange}
        placeholder="Search for a movie"
      />
    </div>
  );
};




