import React,{useState} from "react";
import { Link } from "react-router-dom";
import LoginForm from "./LoginForm"; // Correct import for default export

export const Header = () => {

  const [showLoginForm, setShowLoginForm] = useState(false); // State to track form visibility

  const toggleLoginForm = () => {
    setShowLoginForm(!showLoginForm); // Toggle the visibility of the login form
  };

  return (
    
    <header>
      {/* <Login/> */}
      <div className="container">
        <div className="inner-content">
          <div className="brand">
            <Link to="/">MyWatchList</Link>
          </div>
          <ul className="nav-links">
            <li>
              <Link to="/">Watchlist</Link>
            </li>
            <li>
              <Link to="/watched">Watched</Link>
            </li>
            <li>
              <Link to="/add" className="btn">
                +Add
              </Link>
              

              
             
            </li>
          </ul>
          <div className="navbar-login">
              <button onClick={ <LoginForm/>} className="login-button">
                {showLoginForm ? 'Close' : 'Login'}  {/* Show 'Close' when form is open */}
              </button>
              
            </div>
        </div>
      </div>
    </header>
    
  );
};
