import React, { useContext, useState } from "react";
import { GlobalContext } from "../context/GlobalState";
import { Watched } from "./Watched";

export const ResultCard = ({ movie }) => {
  const { addMovieToWatchlist, addMovieWatched, watchlist, watched,  } = useContext(GlobalContext);
  const [isDisabled, setIsDisabled] = useState(false); // Track disabled state for each button individually

  let storedMovie = watchlist.find((o) => o.imdbID === movie.imdbID); // Ensure you're comparing the right field

   let storedMovieWatched = watched.find((o) => o.imdbID === movie.imdbID); 

  const handleAddToWatchlist = () => {
    addMovieToWatchlist(movie);
    setIsDisabled(true); // Disable only the button of the clicked movie
  };

  const watchedDisabled = storedMovieWatched ? true : false;

  const handleAddToWatched = () => {
    addMovieWatched(movie); // Call the function to add the movie to the watched list
    setIsWatchedDisabled(true); // Disable only the button of the clicked movie
};
  

  
  return (
    <div className="result-card">
      <div className="poster-wrapper">
        {movie.Poster !== "N/A" ? (
          <img src={movie.Poster} alt={`${movie.Title} Poster`} />
        ) : (
          <div className="filler-poster"></div>
        )}
      </div>

      <div className="info">
        <div className="header">
          <h3 className="title">{movie.Title}</h3>
          <h4 className="date">{movie.Year ? movie.Year : "-"}</h4>
        </div>
        <div className="controls">
          <button
            className="btn"
            disabled={isDisabled || !!storedMovie} // Disable if movie is in the watchlist or if the button is clicked
            onClick={handleAddToWatchlist}
          >
            ADD TO WATCHLIST
          </button>

          <button
            className="btn"
            disabled={watchedDisabled} // Disable if movie is in the watchlist or if the button is clicked
            onClick= { handleAddToWatched}
          >
            ADD TO WATCHED
          </button>


        </div>
      </div>
    </div>
  );
};
