import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import LoginForm from './LoginForm'; // Import the LoginForm component
import './Header.css'; // Assuming you have the styling for the header

export const Header = () => {
  const [showLoginForm, setShowLoginForm] = useState(false); // State to track form visibility

  const toggleLoginForm = () => {
    setShowLoginForm((prev) => !prev); // Toggle the visibility of the login form
  };

  return (
    <>
      <header>
        <div className="container">
          <div className="inner-content">
            <div className="brand">
              <Link to="/">MyWatchList</Link>
            </div>
            <ul className="nav-links">
              <li>
                <Link to="/">Watchlist</Link>
              </li>
              <li>
                <Link to="/watched">Watched</Link>
              </li>
              <li>
                <Link to="/add" className="btn">+Add</Link>
              </li>
            </ul>
            <div className="navbar-login">
              <button onClick={toggleLoginForm} className="login-button">
                {showLoginForm ? 'Close' : 'Login'} {/* Show 'Close' when form is open */}
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Conditionally render the LoginForm based on showLoginForm state */}
      {showLoginForm && <LoginForm onClose={toggleLoginForm} />}
    </>
  );
};
