import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // or useNavigate if you're using React Router v6

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const history = useHistory(); // or const navigate = useNavigate(); for React Router v6

  const handleLogin = (e) => {
    e.preventDefault();

    // Example authentication logic (replace with actual authentication)
    if (email === 'test@example.com' && password === 'password123') {
      setError('');
      // Redirect to the watchlist page after successful login
      history.push('/watchlist'); // or navigate('/watchlist') for React Router v6
    } else {
      setError('Invalid email or password');
    }
  };

  return (
    <div className="login-page">
      <h2>Login to Your Movie Watchlist</h2>
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="Enter your email"
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="Enter your password"
          />
        </div>
        {error && <p className="error-message">{error}</p>}
        <button type="submit" className="btn">
          Login
        </button>
      </form>
    </div>
  );
};

export default LoginPage;
